SqlConnection connection = new SqlConnection(MyConnectionString);\nSqlCommand command = new SqlCommand("SELECT [name], [city], [country] FROM People WHERE [id] = @id", connection);\ncommand.Parameters.AddWithValue("@id", someIdValue); // Make sure to replace `someIdValue` with your actual parameter value\n\ntry\n{\n    connection.Open();\n    using (SqlDataReader reader = command.ExecuteReader(System.Data.CommandBehavior.SingleRow))\n    {\n        if (reader.Read())\n        {\n            // Read values.\n            name = reader[0].ToString(); // or reader["name"].ToString();\n            city = reader[1].ToString(); // or reader["city"].ToString();\n            country = reader[2].ToString(); // or reader["country"].ToString();\n        }\n    }\n}\ncatch (Exception)\n{\n    throw;\n}\nfinally\n{\n    if (connection.State == System.Data.ConnectionState.Open)\n    {\n        connection.Close();\n    }\n}