using System.Globalization;\n\nbool IsValidFloat(string value, CultureInfo culture)\n{\n    if (string.IsNullOrEmpty(value))\n        return false;\n\n    float result;\n    // Use TryParse to validate the number considering the specified culture\n    return float.TryParse(value, NumberStyles.Float, culture, out result);\n}\n\n// Usage example in a controller or page code-behind\nprotected void ValidateButton_Click(object sender, EventArgs e)\n{\n    string userInput = myTextBox.Text;\n    CultureInfo userCulture = new CultureInfo("en-US"); // Change to the appropriate culture\n\n    if (!IsValidFloat(userInput, userCulture))\n    {\n        // Handle invalid input (e.g., show error message)\n        ErrorMessageLabel.Text = "Please enter a valid float number.";\n    }\n}