// Create a new instance of the UrlRewriter class\nvar rewriter = new ManagedFusion.Rewriter.UrlRewriter();\n\n// Example rule: Redirect all requests from /oldpage to /newpage\nrewriter.Rules.Add(new ManagedFusion.Rewriter.Rule(\n    pattern: @"^/oldpage$",\n    substitution: "/newpage"));\n\n// Process the incoming request URL\nvar rewrittenUrl = rewriter.GetSubstitutedString("/oldpage", false);\nConsole.WriteLine(rewrittenUrl);  // Outputs: /newpage