protected void ExportToExcelButton_Click(object sender, EventArgs e)\n{\n    // Assuming your HTML table data is already in a DataTable structure named dataTable\n    DataTable dataTable = GetDataFromDatabase(); // Replace with your actual data fetching logic\n\n    Response.Clear();\n    Response.Buffer = true;\n    Response.Charset = "";\n    Response.ContentType = "application/vnd.ms-excel";\n    Response.AddHeader("Content-Disposition", "attachment;filename=YourReport.csv");\n\n    using (var sw = new StringWriter())\n    {\n        var writer = new CsvWriter(sw, CultureInfo.InvariantCulture);\n        writer.WriteRecords(dataTable.AsEnumerable().Select(row => \n            row.ItemArray.Select(field => field.ToString()).ToArray()));\n        Response.Output.Write(sw.ToString());\n    }\n\n    Response.Flush();\n    Response.End();\n}\n\nprivate DataTable GetDataFromDatabase()\n{\n    // Implement your database call to fetch data here\n    return new DataTable(); // Return actual DataTable fetched from DB\n}