using System;\nusing System.Net;\n\nclass Program\n{\n    static void Main()\n    {\n        string url = "http://www.mysite.com";\n        WebClient webClient = new WebClient();\n\n        // Set up the proxy to use default credentials\n        WebProxy proxy = (WebProxy)WebRequest.DefaultWebProxy;\n        proxy.UseDefaultCredentials = true; // Use system's default credentials for authentication\n        webClient.Proxy = proxy;\n\n        try\n        {\n            byte[] data = webClient.DownloadData(url);\n            Console.WriteLine("Download successful.");\n        }\n        catch (WebException ex)\n        {\n            if (ex.Status == WebExceptionStatus.ProtocolError &&\n                ((HttpWebResponse)ex.Response).StatusCode == HttpStatusCode.ProxyAuthenticationRequired)\n            {\n                Console.WriteLine("Proxy authentication required. Please check your proxy settings in Internet Explorer.");\n            }\n            else\n            {\n                Console.WriteLine($"An error occurred: {ex.Message}");\n            }\n        }\n    }\n}