using System;\nusing System.Data;\nusing Oracle.ManagedDataAccess.Client;\n\npublic void BulkInsert(DataTable dataTable, string connectionString)\n{\n    using (OracleConnection connection = new OracleConnection(connectionString))\n    {\n        connection.Open();\n\n        using (OracleBulkCopy bulkCopy = new OracleBulkCopy(connection))\n        {\n            // Map columns\n            foreach (DataColumn column in dataTable.Columns)\n            {\n                bulkCopy.ColumnMappings.Add(column.ColumnName, column.ColumnName);\n            }\n\n            // Set the destination table name\n            bulkCopy.DestinationTableName = "YourDestinationTable";\n\n            try\n            {\n                // Write from the data table to destination table\n                bulkCopy.WriteToServer(dataTable);\n            }\n            catch (Exception ex)\n            {\n                Console.WriteLine("An error occurred: {0}", ex.Message);\n            }\n        }\n\n        connection.Close();\n    }\n}