protected void GridView1_RowDataBound(object sender, GridViewRowEventArgs e)\n{\n    if (e.Row.RowType == DataControlRowType.DataRow)\n    {\n        // Assume your varBinary field is named "VarBinaryColumn"\n        byte[] byteArray = (byte[])DataBinder.Eval(e.Row.DataItem, "VarBinaryColumn");\n        \n        // Convert the byte array to a hexadecimal string\n        string hexString = BitConverter.ToString(byteArray).Replace("-", "").ToUpper();\n        \n        // Bind the hexadecimal string to a label or any other control in your GridView\n        Label lblHexData = (Label)e.Row.FindControl("lblHexData"); // Ensure you have this Label in your GridView template\n        lblHexData.Text = hexString;\n    }\n}