using System;\nusing System.IO.Pipes;\nusing System.Text;\nusing System.Threading.Tasks;\n\nclass UiServer {\n    private const string管道名称 = @"\\.\pipe\MyInterProcessCommunicationChannel";\n\n    public void Run() {\n        Task.Run(async () => {\n            while (true) {\n                using var pipeServer = new NamedPipeServerStream(管道名称, PipeDirection.In);\n                await pipeServer.WaitForConnectionAsync();\n\n                using var reader = new StreamReader(pipeServer);\n                string text;\n                while ((text = await reader.ReadLineAsync()) != null) {\n                    // Here you can display the message box\n                    System.Windows.Forms.MessageBox.Show(text);\n                }\n            }\n        });\n    }\n}