using System;\nusing System.Runtime.InteropServices;\n\nclass Program\n{\n    static void Main()\n    {\n        long width = 19005;\n        long height = 32768;\n        long size = (width * height * 4) - 1;\n\n        // Allocate memory block of the required size\n        IntPtr ptr = Marshal.AllocHGlobal((IntPtr)size);\n\n        try\n        {\n            // Use the allocated memory...\n            // Example: Set a byte at a specific position (e.g., position 1024)\n            Marshal.WriteByte(ptr, 1024, 255);\n\n            // Access a byte from the allocated memory...\n            byte b = Marshal.ReadByte(ptr, 1024);\n\n            Console.WriteLine("Byte at position 1024: " + b);\n        }\n        finally\n        {\n            // Free the allocated memory to avoid leaks\n            Marshal.FreeHGlobal(ptr);\n        }\n\n        Console.ReadLine();\n    }\n}