using System.Windows.Automation;\nusing Microsoft.VisualStudio.TestTools.UnitTesting;\n\n[TestClass]\npublic class UITests\n{\n    [TestMethod]\n    public void TestButton_Click()\n    {\n        // Start the application\n        Application.Launch(@"C:\Path\To\Your\Application.exe");\n\n        // Find a button by its automation ID or Name\n        AutomationElement button = AutomationElement.RootElement.FindFirst(TreeScope.Descendants,\n            new PropertyCondition(AutomationElement.AutomationIdProperty, "button1"));\n\n        // Invoke the Click action on the button\n        var invokePattern = (InvokePattern)button.GetCurrentPattern(InvokePattern.Pattern);\n        invokePattern.Invoke();\n\n        // Optional: Verify some changes after clicking\n        AutomationElement label = AutomationElement.RootElement.FindFirst(TreeScope.Descendants,\n            new PropertyCondition(AutomationElement.AutomationIdProperty, "label1"));\n\n        Assert.AreEqual("Expected Text", (string)label.GetCurrentPropertyValue(AutomationElement.NameProperty));\n    }\n}