using System.Data.Common;\nusing System.Data.Entity.Infrastructure.Interception;\n\npublic class SQLLogger : IDbCommandInterceptor\n{\n    public void NonQueryExecuting(DbCommand command, DbCommandInterceptionContext<int> interceptionContext)\n    {\n        LogSql(command);\n    }\n\n    public void ScalarExecuting(DbCommand command, DbCommandInterceptionContext<object> interceptionContext)\n    {\n        LogSql(command);\n    }\n\n    public void ReaderExecuting(DbCommand command, DbCommandInterceptionContext<DbDataReader> interceptionContext)\n    {\n        LogSql(command);\n    }\n\n    private static void LogSql(DbCommand command)\n    {\n        System.Diagnostics.Debug.WriteLine(command.CommandText);\n        // Or log to a file or anywhere else you prefer\n    }\n}\n\n// Register the interceptor in your app startup\nDbInterception.Add(new SQLLogger());