using System.ComponentModel;\nusing DevExpress.XtraGrid.Views.Grid;\n\npublic partial class MainForm : Form {\n    private YourDataContext db = new YourDataContext();\n    private BindingList<profile> profileBindingList;\n\n    public MainForm() {\n        InitializeComponent();\n\n        // Fetch the profiles from the database and convert it to a BindingList\n        profileBindingList = new BindingList<profile>(db.profiles.ToList());\n        \n        // Bind the grid to the binding list\n        dataUltraGrid.DataSource = profileBindingList;\n    }\n\n    private void SaveChangesButton_Click(object sender, EventArgs e) {\n        db.SubmitChanges();\n    }\n}