using System;\nusing System.Runtime.InteropServices;\n\nnamespace DotNetInterop\n{\n    [Guid("YOUR-GUID-HERE")]\n    [InterfaceType(ComInterfaceType.InterfaceIsIDispatch)]\n    public interface ICallableDotNetFunction\n    {\n        object Execute(string assemblyPath, string className, string methodName, params object[] parameters);\n    }\n\n    [Guid("YOUR-GUID- HERE")]\n    [ClassInterface(ClassInterfaceType.None)]\n    [ProgId("DotNetInterop.Executor")]\n    public class Executor : ICallableDotNetFunction\n    {\n        public object Execute(string assemblyPath, string className, string methodName, params object[] parameters)\n        {\n            var assembly = System.Reflection.Assembly.LoadFile(assemblyPath);\n            var type = assembly.GetType(className);\n            var methodInfo = type.GetMethod(methodName);\n            return methodInfo.Invoke(null, parameters);\n        }\n    }\n}