using System;\nusing System.Web.Services;\nusing System.Web.Script.Services;\nusing System.Web.Script.Serialization;\n\n[WebService(Namespace = "http://tempuri.org/")]\npublic class JsonWebService : WebService\n{\n    [WebMethod]\n    [ScriptMethod(ResponseFormat = ResponseFormat.Json)]\n    public string GetJsonData()\n    {\n        // Create an object to serialize to JSON\n        var data = new { Name = "John Doe", Age = 30 };\n        \n        // Serialize the object to a JSON string\n        JavaScriptSerializer serializer = new JavaScriptSerializer();\n        return serializer.Serialize(data);\n    }\n\n    [WebMethod]\n    public void PostJsonData(string jsonData)\n    {\n        // Deserialize the JSON string back into an object\n        JavaScriptSerializer serializer = new JavaScriptSerializer();\n        var data = (System.Collections.Generic.IDictionary<string, object>)serializer.Deserialize(jsonData, typeof(System.Collections.Generic.IDictionary<string, object>));\n        \n        // Access the deserialized data\n        string name = data["Name"].ToString();\n        int age = Convert.ToInt32(data["Age"]);\n    }\n}