private delegate void UpdateUI();\nprivate void LoadMapAsync()\n{\n    // Show loading dialog or progress bar here (on UI thread)\n    this.Invoke(new UpdateUI(delegate { progressBar1.Visible = true; }));\n\n    // Run the map creation in a background thread\n    ThreadPool.QueueUserWorkItem((state) =>\n    {\n        try\n        {\n            axMappointControl1.NewMap(MapPoint.GeoMapRegion.geoMapNorthAmerica);\n\n            // Hide loading dialog or progress bar after load is complete (back on UI thread)\n            this.Invoke(new UpdateUI(delegate \n            { \n                progressBar1.Visible = false; \n            }));\n        }\n        catch (Exception ex)\n        {\n            // Handle exceptions and possibly inform the user\n            this.Invoke(new UpdateUI(delegate \n            { \n                MessageBox.Show("Failed to load map: " + ex.Message); \n            }));\n        }\n    });\n}\n\n// Call LoadMapAsync() where you need to create a new map.