using OfficeOpenXml;\nusing System.IO;\n\npublic void AddImageToExcel()\n{\n    // Create a new ExcelPackage (workbook)\n    using (var package = new ExcelPackage())\n    {\n        // Add a new worksheet\n        var worksheet = package.Workbook.Worksheets.Add("Sheet1");\n\n        // Load an image from file or stream\n        byte[] imageBytes = File.ReadAllBytes(Server.MapPath("~/path/to/your/image.jpg"));\n\n        // Insert the image into the worksheet\n        var picture = worksheet.Drawings.AddPicture("Test", imageBytes);\n\n        // Position the image\n        picture.SetPosition(0, 0); // Row col position\n        picture.SetSize(640, 480); // Width and height pixels\n\n        // Write file to output stream or directly\n        using (var memoryStream = new MemoryStream())\n        {\n            package.SaveAs(memoryStream);\n            Response.Clear();\n            Response.ContentType = "application/vnd.openxmlformats-officedocument.spreadsheetml.sheet";\n            Response.AddHeader("content-disposition", "attachment; filename=FileName.xlsx");\n            Response.BinaryWrite(memoryStream.ToArray());\n            Response.End();\n        }\n    }\n}