using System;\nusing System.IO;\n\npublic class ServiceLogic\n{\n    private string GetXmlFilePath()\n    {\n        // Retrieve the path to Common Application Data folder\n        string commonAppDataPath = Environment.GetFolderPath(Environment.SpecialFolder.CommonApplicationData);\n        \n        // Combine with your application-specific subfolder and file name\n        return Path.Combine(commonAppDataPath, "YourAppName", "yourfile.xml");\n    }\n\n    public void LoadXmlFile()\n    {\n        string filePath = GetXmlFilePath();\n        if (File.Exists(filePath))\n        {\n            // Logic to load XML file\n        }\n        else\n        {\n            throw new FileNotFoundException("The configuration file was not found.", filePath);\n        }\n    }\n}