using System;\nusing Opc.UaFx.Client;\n\nclass Program\n{\n    static void Main(string[] args)\n    {\n        // Create a client instance configured to connect the specified node.\n        using (var client = new OpcClient("opc.tcp://localhost:4840"))\n        {\n            try\n            {\n                // Connect to the server and read a value from a node on the server.\n                client.Connect();\n                var nodeId = "ns=2;s=MyVariable";\n                object value = client.ReadNode(nodeId);\n                Console.WriteLine("Value of '{0}' is: {1}", nodeId, value);\n            }\n            catch (OpcException ex)\n            {\n                // Handle OPC UA exceptions\n                Console.WriteLine(ex.Message);\n            }\n        }\n    }\n}