private void backgroundWorker1_DoWork(object sender, System.ComponentModel.DoWorkEventArgs e)\n{\n    Thread comThread = new Thread(() =>\n    {\n        COMComponent.LongRunningMethod();\n    });\n    \n    // Ensure to initialize STA for the thread if it's an STA-compliant COM component\n    comThread.SetApartmentState(ApartmentState.STA);\n    comThread.Start();\n\n    // Optionally, wait until the operation completes depending on your needs\n    comThread.Join();\n}