using System;\nusing System.IO;\n\nclass Program\n{\n    static void Main()\n    {\n        string filePath = @"C:\Project\StorageDirectory\file.txt";\n        \n        try\n        {\n            DriveInfo driveInfo = new DriveInfo(Path.GetPathRoot(filePath));\n            if (driveInfo.DriveFormat != "NTFS")\n                throw new IOException("File must be on an NTFS volume to use encryption.");\n\n            File.Encrypt(filePath);\n            Console.WriteLine("File encrypted successfully.");\n        }\n        catch (Exception ex)\n        {\n            Console.WriteLine($"An error occurred: {ex.Message}");\n            if (ex.InnerException != null)\n                Console.WriteLine($"Inner Exception: {ex.InnerException.Message}");\n        }\n    }\n}