using System;\nusing System.IO;\nusing System.Security.AccessControl;\n\nclass Program\n{\n    static void Main()\n    {\n        string filePath = @"C:\path\to\your\file.txt";\n        \n        // Get the current security settings for the file\n        FileSecurity security = File.GetAccessControl(filePath);\n        \n        // Create a new rule granting read/write access to everyone\n        FileSystemAccessRule rule = new FileSystemAccessRule(\n            "Everyone",\n            FileSystemRights.Read | FileSystemRights.Write,\n            InheritanceFlags.None,\n            PropagationFlags.NoPropagateInherit,\n            AccessControlType.Allow);\n        \n        // Add the access control entry to the security settings\n        security.AddAccessRule(rule);\n        \n        // Apply the modified security settings to the file\n        File.SetAccessControl(filePath, security);\n\n        Console.WriteLine("Permissions updated successfully.");\n    }\n}