// Code-behind in C#\nprotected string UniqueSessionValue;\n\nprotected void Page_Load(object sender, EventArgs e)\n{\n    if (!IsPostBack)\n    {\n        // Generate a unique value each time the page is loaded initially.\n        UniqueSessionValue = Guid.NewGuid().ToString();\n        Session["UniqueSubmitId"] = UniqueSessionValue;\n    }\n}\n\nprotected void btnSubmit_Click(object sender, EventArgs e)\n{\n    string submittedUniqueId = Request.Form["uniqueIdField"];\n    string expectedUniqueId = (string)Session["UniqueSubmitId"];\n\n    if (!string.IsNullOrEmpty(submittedUniqueId) && submittedUniqueId == expectedUniqueId)\n    {\n        // Process the insert operation here\n        InsertMessage(txtMessage.Text);\n\n        // Clear session to prevent resubmission on refresh or back button.\n        Session.Remove("UniqueSubmitId");\n\n        Response.Redirect("~/YourForumPage.aspx");\n    }\n    else\n    {\n        // Handle duplicate submission or invalid state\n        lblError.Text = "Duplicate submission detected.";\n    }\n}\n\nprivate void InsertMessage(string message)\n{\n    // Your logic to insert into the database here\n}