using System;\nusing System.Collections.Generic;\n\npublic class OpenIdValidator\n{\n    private readonly List<string> _allowedDomains = new List<string>\n    {\n        "example.com",\n        "trustedprovider.org"\n    };\n\n    public bool IsValidOpenId(string openIdUri)\n    {\n        if (string.IsNullOrEmpty(openIdUri))\n        {\n            return false;\n        }\n\n        // Check if the URI is well-formed\n        if (!Uri.TryCreate(openIdUri, UriKind.Absolute, out var uri) || uri.Scheme != "https")\n        {\n            return false;\n        }\n\n        // Optionally check against allowed domains\n        return _allowedDomains.Contains(uri.Host);\n    }\n}\n\n// Usage example\nvar validator = new OpenIdValidator();\nbool isValid = validator.IsValidOpenId("https://example.com/user/openid");