private void MyRTB_TextChanged(object sender, TextChangedEventArgs e)\n{\n    RichTextBox rtb = sender as RichTextBox;\n    if (rtb != null)\n    {\n        TextPointer navigator = rtb.Document.ContentStart;\n        while (navigator.CompareTo(rtb.Document.ContentEnd) < 0)\n        {\n            TextPointerContext context = navigator.GetPointerContext(LogicalDirection.Forward);\n            if (context == TextPointerContext.Text)\n            {\n                string textRun = navigator.GetTextInRun(LogicalDirection.Forward);\n                Run run = new Run(textRun);\n\n                // Apply color based on character position\n                int offset = rtb.Document.ContentStart.GetOffsetToPosition(navigator);\n                if (offset < 50) // Change color at or after 50 characters\n                {\n                    run.Foreground = Brushes.Black;\n                }\n                else\n                {\n                    run.Foreground = Brushes.Red;\n                }\n\n                navigator.GetTextElement(2).Blocks.Clear();\n                navigator.GetTextElement(2).Inlines.Add(run);\n            }\n        }\n    }\n}