using System;\nusing System.Web.Services;\nusing System.Web.Services.Protocols;\n\n[WebService(Namespace = "http://example.com/Verify/")]\npublic class VerificationService : WebService\n{\n    private const string ValidApiKey = "your-secure-api-key-here";\n\n    [WebMethod]\n    public string Verify(string apiKey, int accountNumber, int productName)\n    {\n        // Check if the provided API key is valid\n        if (apiKey != ValidApiKey)\n        {\n            throw new SoapException("Invalid API Key", SoapException.ClientFaultCode);\n        }\n\n        // Perform your verification logic here\n        return "Verification passed for AccountNumber: " + accountNumber + ", ProductName: " + productName;\n    }\n}