using System;\nusing System.Data;\nusing Oracle.ManagedDataAccess.Client;\n\npublic void FetchPagedData(int pageNumber, int rowsPerPage)\n{\n    using (OracleConnection connection = new OracleConnection("your_connection_string"))\n    {\n        connection.Open();\n        using (OracleCommand cmd = new OracleCommand("GetPagedData", connection))\n        {\n            cmd.CommandType = CommandType.StoredProcedure;\n            cmd.Parameters.Add(new OracleParameter("p_page_number", pageNumber));\n            cmd.Parameters.Add(new OracleParameter("p_rows_per_page", rowsPerPage));\n            \n            // Output cursor parameter\n            OracleParameter outputCursor = cmd.Parameters.Add("p_result", OracleDbType.RefCursor, ParameterDirection.Output);\n\n            using (OracleDataReader reader = cmd.ExecuteReader())\n            {\n                DataTable dataTable = new DataTable();\n                dataTable.Load(reader);\n                // Process your data here\n                Console.WriteLine(dataTable.Rows.Count + " rows fetched.");\n            }\n        }\n    }\n}