using HtmlAgilityPack;\nusing System.Text.RegularExpressions;\n\npublic string SanitizeHTML(string content)\n{\n    var sanitizer = new HtmlSanitizer();\n    return sanitizer.Sanitize(content);\n}\n\nprotected void submitButton_Click(object sender, EventArgs e)\n{\n    string htmlContent = Request.Form["myTextArea"];\n    \n    // Sanitize the HTML\n    string safeHtmlContent = SanitizeHTML(htmlContent);\n\n    System.IO.File.WriteAllText(Server.MapPath("~/output.html"), safeHtmlContent);\n}