// Using CultureInfo to get month names\nvar months = System.Globalization.CultureInfo.CurrentCulture.DateTimeFormat.MonthGenitiveNames;\nvar monthList = new List<KeyValuePair<string, string>>();\n\nfor (int i = 0; i < 12; i++)\n{\n    monthList.Add(new KeyValuePair<string, string>((i + 1).ToString("D2"), months[i]));\n}\n\n// Assuming you have a DropDownList control named "MonthDropDown"\nMonthDropDown.DataSource = monthList;\nMonthDropDown.DataTextField = "Value"; // The display text for the dropdown\nMonthDropDown.DataValueField = "Key";   // The value (month number) associated with each item\nMonthDropDown.DataBind();