protected void Page_Load(object sender, EventArgs e)\n{\n    if (!IsPostBack)\n    {\n        foreach (var control in this.Controls)\n        {\n            ApplyValidatorSettings(control);\n        }\n    }\n}\n\nprivate static void ApplyValidatorSettings(Control parentControl)\n{\n    foreach (Control control in parentControl.Controls)\n    {\n        if (control is WebControls.BaseValidator validator)\n        {\n            validator.ForeColor = System.Drawing.Color.Empty; // Reset ForeColor to use CSS\n            validator.SetFocusOnError = false;\n        }\n        ApplyValidatorSettings(control); // Recursively apply settings to child controls\n    }\n}