using System;\nusing System.Collections.Generic;\nusing System.Linq;\n\npublic class Person\n{\n    public string Lastname { get; set; }\n}\n\nclass Program\n{\n    static void Main()\n    {\n        List<Person> people = PopulateList();\n\n        // Sorting using OrderBy and storing the result in a new list\n        var sortedPeople = people.OrderBy(p => p.Lastname).ToList();\n\n        // If you want to sort the original list in place, use Sort with a Comparison:\n        people.Sort((p1, p2) => string.Compare(p1.Lastname, p2.Lastname, StringComparison.Ordinal));\n    }\n\n    static List<Person> PopulateList()\n    {\n        // Example list population\n        List<Person> list = new List<Person>\n        {\n            new Person { Lastname = "Doe" },\n            new Person { Lastname = "Jane" },\n            new Person { Lastname = "Smith" }\n        };\n        return list;\n    }\n}