using System;\n\npublic class MyClass<T>\n{\n    public MyClass()\n    {\n        if (!IsSerializable(typeof(T)))\n        {\n            throw new InvalidOperationException($"Type {typeof(T).Name} does not have the Serializable attribute.");\n        }\n    }\n\n    private bool IsSerializable(Type type)\n    {\n        return Attribute.IsDefined(type, typeof(System.SerializableAttribute));\n    }\n}