using System;\nusing System.Runtime.InteropServices;\n\nclass Program {\n    [DllImport("kernel32.dll", SetLastError = true)]\n    static extern IntPtr GetConsoleWindow();\n\n    [DllImport("user32.dll")]\n    static extern bool EnableMenuItem(IntPtr hMenu, uint uIDEnableItem, uint uEnable);\n\n    const int MF_GRAYED = 0x00000001;\n    \n    // Constants for console menu items\n    const int SC_SIZE        = 0xF000;\n    const int SC_MOVE        = 0xF010;\n    const int SC_MINIMIZE    = 0xF020;\n    const int SC_MAXIMIZE    = 0xF030;\n    const int SC_CLOSE       = 0xF060;\n\n    static void Main(string[] args) {\n        IntPtr consoleHandle = GetConsoleWindow();\n        if (consoleHandle != IntPtr.Zero) {\n            // Disable resizing, moving and closing the window\n            IntPtr hMenu = System.Diagnostics.Process.GetCurrentProcess().MainWindowHandle;\n            EnableMenuItem(hMenu, SC_SIZE, MF_GRAYED);\n            EnableMenuItem(hMenu, SC_MOVE, MF_GRAYED);\n            EnableMenuItem(hMenu, SC_MINIMIZE, MF_GRAYED);\n            EnableMenuItem(hMenu, SC_MAXIMIZE, MF_GRAYED);\n            EnableMenuItem(hMenu, SC_CLOSE, MF_GRAYED);\n            \n            Console.WriteLine("Console options are disabled.");\n        }\n        \n        // Your application logic here\n    }\n}