using System.Drawing;\nusing System.Drawing.Drawing2D;\n\npublic void DrawTextOnPdf(Graphics pdfGraphics, string text, float fontSizeInPoints)\n{\n    // Assuming pdfGraphics is already set up with the correct page settings\n\n    // Create a StringFormat object to align and measure text strings.\n    StringFormat sf = new StringFormat();\n\n    // Set any required properties on the format (e.g., alignment).\n    sf.Alignment = StringAlignment.Near;\n    sf.LineAlignment = StringAlignment.Center;\n\n    // Scale the font size if necessary. Here we assume a standard scaling factor.\n    float scaleFactor = 1; // Determine your scale factor based on transformations\n\n    // Create a Font object with the desired font, style, and size (in points).\n    using (Font font = new Font("Arial", fontSizeInPoints * scaleFactor))\n    {\n        // Draw the string.\n        pdfGraphics.DrawString(text, font, Brushes.Black, 0, 0, sf);\n    }\n}