using System;\nusing System.Linq;\nusing System.Security.Cryptography;\n\npublic class RandomFolderNameGenerator {\n    private static readonly char[] ValidChars = "ABCDEFGHIJKLMNOPQRSTUVWXYZabcdefghijklmnopqrstuvwxyz0123456789".ToCharArray();\n\n    public static string GenerateRandomFolderName(int length) {\n        using (var rng = new RNGCryptoServiceProvider()) {\n            var byteBuffer = new byte[length * 5 / 8];\n            rng.GetBytes(byteBuffer);\n            return new string(Enumerable.Range(0, length)\n                .Select(i => ValidChars[byteBuffer[i * 8 / 5] >> (32 - (i % 8) * 4 - 4) & 15]).ToArray());\n        }\n    }\n\n    public static void Main() {\n        Console.WriteLine("Random Folder Name: " + GenerateRandomFolderName(12));\n    }\n}