using System.Net.Mail;\n\nMailMessage msg = new MailMessage();\nmsg.From = new MailAddress("sender@example.com");\nmsg.To.Add("recipient@example.com");\nmsg.Subject = "Request Details";\n\nstring bodyTemplate = @"\n<html>\n<body>\n    <p><strong>Date of Hire:</strong> %HireDate%</p>\n    <p><strong>Annual Salary:</strong> %AnnualIncome%</p>\n    <p><strong>Reason for Request:</strong> %ReasonForRequest%</p>\n    <br/>\n    <p><strong>Name of Voluntary Employee:</strong> %FirstName% %LastName%</p>\n    <p><strong>Total Coverage Applied For:</strong> %EECoverageAmount%</p>\n    <p><strong>Guaranteed Coverage Portion:</strong> %GICoveragePortion%</p>\n    <p><strong>Amount Subject to Medical Evident:</strong> %GIOverage%</p>\n</body>\n</html>";\n\nstring body = bodyTemplate.Replace("%HireDate%", "9/28/2001")\n                          .Replace("%AnnualIncome%", "$100,000")\n                          .Replace("%ReasonForRequest%", "New Hire")\n                          .Replace("%FirstName%", "Ronald")\n                          .Replace("%LastName%", "Weasley")\n                          .Replace("%EECoverageAmount%", "$500,000")\n                          .Replace("%GICoveragePortion%", "$300,000.00")\n                          .Replace("%GIOverage%", "$200,000");\n\nmsg.Body = body;\nmsg.IsBodyHtml = true;\n\nSmtpClient client = new SmtpClient("smtp.yourserver.com");\nclient.Send(msg);