[WebMethod(Description = "Main entry point to the service.")]\npublic string MethodA(string theXmlInput)\n{\n    DateTime startTime = DateTime.Now;\n\n    // Log first action start time\n    StoreInput(theXmlInput, startTime);\n\n    // Run the job, should take about 1 sec\n    string result = DoIt(theXmlInput);\n    DateTime afterDoItTime = DateTime.Now;\n    \n    // Log output and processing end time\n    StoreResult(result, afterDoItTime);\n\n    DateTime endTime = DateTime.Now;\n\n    Console.WriteLine($"Total time: {(endTime - startTime).TotalMilliseconds} ms");\n    Console.WriteLine($"DoIt method took: {(afterDoItTime - startTime).TotalMilliseconds} ms");\n\n    return result;\n}