using System;\nusing System.Diagnostics;\n\nclass Program {\n    static void Main(string[] args) {\n        try {\n            // Kill all iexplore processes\n            Process[] ieProcesses = Process.GetProcessesByName("iexplore");\n            \n            foreach (Process process in ieProcesses) {\n                process.Kill();\n                process.WaitForExit(); // Wait for the IE to exit completely\n            }\n        } catch (Exception e) {\n            Console.WriteLine("Failed to kill IE instances: " + e.Message);\n        }\n    }\n}