using System.Drawing;\nusing System.Windows.Forms;\n\npublic Bitmap CaptureControl(Control control)\n{\n    // Create a bitmap from the bounds of the control\n    var bmp = new Bitmap(control.Width, control.Height);\n\n    // Use the Graphics object to draw the control onto the bitmap\n    using (Graphics g = Graphics.FromImage(bmp))\n    {\n        // Draw the control onto the graphics object for the bitmap\n        g.DrawRectangle(Pens.White, 0, 0, bmp.Width - 1, bmp.Height - 1);\n        control.DrawToBitmap(bmp, control.ClientRectangle);\n    }\n\n    return bmp;\n}