public class ZipCodeManager\n{\n    private HashSet<int> _addedZipCodes = new HashSet<int>();\n    private HashSet<int> _removedZipCodes = new HashSet<int>();\n\n    public void AddZipCode(int zipCode)\n    {\n        _addedZipCodes.Add(zipCode);\n        _removedZipCodes.Remove(zipCode); // In case it was removed earlier\n    }\n\n    public void RemoveZipCode(int zipCode)\n    {\n        _removedZipCodes.Add(zipCode);\n        _addedZipCodes.Remove(zipCode); // In case it was added earlier\n    }\n\n    public bool HasChanges => _addedZipCodes.Count > 0 || _removedZipCodes.Count > 0;\n\n    public List<int> GetAddedZipCodes()\n    {\n        return new List<int>(_addedZipCodes);\n    }\n\n    public List<int> GetRemovedZipCodes()\n    {\n        return new List<int>(_removedZipCodes);\n    }\n}