// On Save button click event\nif (_zipCodeManager.HasChanges)\n{\n    // Get the changes\n    List<int> addedZipCodes = _zipCodeManager.GetAddedZipCodes();\n    List<int> removedZipCodes = _zipCodeManager.GetRemovedZipCodes();\n\n    // Process inserts for added zip codes\n    foreach (var zip in addedZipCodes)\n    {\n        string queryInsert = "INSERT INTO LocationZipCodes(LocationId, ZipCode) VALUES(@LocationId, @ZipCode)";\n        SQLHelper.ExecuteNonQuery(queryInsert, new SqlParameter("@LocationId", locationId), new SqlParameter("@ZipCode", zip));\n    }\n\n    // Process deletes for removed zip codes\n    foreach (var zip in removedZipCodes)\n    {\n        string queryDelete = "DELETE FROM LocationZipCodes WHERE LocationId=@LocationId AND ZipCode=@ZipCode";\n        SQLHelper.ExecuteNonQuery(queryDelete, new SqlParameter("@LocationId", locationId), new SqlParameter("@ZipCode", zip));\n    }\n\n    // Clear changes after processing\n    _zipCodeManager.ClearChanges();\n}