// Using Non-Nullable Type with Default Value for Optional Scenarios\npublic void ProcessFileName(string fileName)\n{\n    // Assuming string.Empty is the default and represents no file name\n    if (string.IsNullOrEmpty(fileName))\n    {\n        // Handle missing filename case\n        return;\n    }\n    // Process the filename\n}\n\n// Using Nullable Type\npublic void ProcessNullableFileName(string? fileName)\n{\n    // Directly check for null\n    if (fileName == null)\n    {\n        // Handle missing filename case\n        return;\n    }\n    // Process the filename\n}