using System;\nusing System.Collections.Generic;\n\npublic static class DictionaryWrapper\n{\n    private static readonly IDictionary<string, object> m_Dictionary = new Dictionary<string, object>();\n\n    public static object this[string key]\n    {\n        get\n        {\n            if (m_Dictionary.ContainsKey(key))\n            {\n                return m_Dictionary[key];\n            }\n            else\n            {\n                return string.Empty;\n            }\n        }\n        set\n        {\n            if (m_Dictionary.ContainsKey(key))\n            {\n                m_Dictionary[key] = value;\n            }\n            else\n            {\n                m_Dictionary.Add(key, value);\n            }\n        }\n    }\n}