// Create an OrExpression for the nested OR conditions\nvar orExpression = new OrExpression();\n\n// Add each BETWEEN condition to the OrExpression\ncriteria.TaxCreditApprovalYear.ForEach(year =>\n{\n    var betweenCondition = new BetweenAndSubSonic.Where(ExpressionType.And,\n        ExpressionTemplate.BetweenAnd, \n        Property_Overview.Columns.EffectiveDate, \n        new DateTime(year, 01, 01), \n        new DateTime(year, 12, 31));\n    orExpression.Expressions.Add(betweenCondition);\n});\n\n// Add the OrExpression to your query with an AND\nqry.And(orExpression);