// Define a dictionary of valid columns\nDictionary<string, string> validColumns = new Dictionary<string, string>\n{\n    { "title", "Title" },\n    { "description", "Description" },\n    { "status", "Status" }\n    // Add all other valid column names here\n};\n\nstring fieldName = Request.QueryString["fieldname"];\nstring columnName;\n\n// Check if the fieldname from querystring is in the list of valid columns\nif (!validColumns.TryGetValue(fieldName, out columnName))\n{\n    throw new ArgumentException("Invalid field name provided.");\n}\n\nusing (SqlConnection conn = new SqlConnection(connectionString))\n{\n    using (SqlCommand cmd = new SqlCommand())\n    {\n        cmd.Connection = conn;\n        \n        // Use parameterized query to prevent SQL injection\n        cmd.CommandText = "UPDATE MyTable SET " + columnName + " = @fieldvalue WHERE id = @id";\n        cmd.Parameters.AddWithValue("@fieldvalue", Request.QueryString["fieldvalue"]); // Ensure this is also validated and sanitized\n        cmd.Parameters.AddWithValue("@id", Request.QueryString["id"]);\n\n        conn.Open();\n        int result = cmd.ExecuteNonQuery();\n    }\n}