using System;\nusing WMPLib;\n\nclass Program\n{\n    static void Main()\n    {\n        // Create an instance of Windows Media Player.\n        var player = new WindowsMediaPlayer();\n\n        // Open a media file to play.\n        // Replace "yourfile.mp3" with your actual media file path.\n        player.URL = @"C:\path\to\yourfile.mp3";\n\n        // Play the media once it's open.\n        player.controls.play();\n\n        // Set playback rate (e.g., 1.5 for faster playback).\n        player.settings.rate = 1.5;\n\n        // To ensure the application waits and does not exit immediately:\n        Console.WriteLine("Press any key to stop playing...");\n        Console.ReadKey();\n\n        // Stop the media.\n        player.controls.stop();\n    }\n}