using System;\nusing CoreAudioApi;\n\nclass Program\n{\n    static void Main()\n    {\n        MMDeviceEnumerator devEnum = new MMDeviceEnumerator();\n        MMDevice defaultRecordingDevice = devEnum.GetDefaultAudioEndpoint(EDataFlow.eCapture, ERole.eCommunications);\n\n        if (defaultRecordingDevice != null)\n        {\n            Console.WriteLine("A default recording device is set: " + defaultRecordingDevice.FriendlyName);\n        }\n        else\n        {\n            Console.WriteLine("No default recording device detected.");\n        }\n    }\n}