using System;\nusing System.IO;\nusing System.Xml;\nusing System.Xml.Schema;\n\nclass Program\n{\n    static void Main()\n    {\n        string xmlFile = "largefile.xml";\n        string xsdFile = "schema.xsd";\n\n        XmlReaderSettings settings = new XmlReaderSettings();\n        settings.ValidationType = ValidationType.Schema;\n        settings.Schemas.Add(null, xsdFile);\n        settings.ValidationEventHandler += Settings_ValidationEventHandler;\n\n        using (XmlReader reader = XmlReader.Create(xmlFile, settings))\n        {\n            while (reader.Read())\n            {\n                // No need to do anything specific here, validation is handled automatically\n            }\n        }\n\n        Console.WriteLine("Validation complete.");\n    }\n\n    private static void Settings_ValidationEventHandler(object sender, ValidationEventArgs e)\n    {\n        switch (e.Severity)\n        {\n            case XmlSeverityType.Warning:\n                Console.WriteLine($"WARNING: {e.Message}");\n                break;\n            case XmlSeverityType.Error:\n                Console.WriteLine($"ERROR: {e.Message}");\n                break;\n        }\n    }\n}