using UnitsNet;\nusing UnitsNet.Densitys;\nusing UnitsNet.Units;\n\nclass Program\n{\n    static void Main()\n    {\n        var m1 = new Mass(123.00, MassUnit.Pound);\n        var m2 = new Volume(123.00, VolumeUnit.Liter);\n\n        // Convert 123 pounds to kilograms\n        var massInKilograms = m1.ToUnit(MassUnit.Kilogram);\n        Console.WriteLine($"Mass in kilograms: {massInKilograms}");\n\n        // Convert 123 liters to pounds given a density of 7.0 pounds per gallon\n        // First convert the volume from liters to gallons\n        var volumeInGallons = m2.ToUnit(VolumeUnit.Gallon);\n        var density = Density.FromPoundsPerGallon(7.0);\n\n        // Calculate mass in pounds using the formula: Mass = Volume * Density\n        var massInPounds = volumeInGallons * density;\n        Console.WriteLine($"Mass in pounds: {massInPounds.ToUnit(MassUnit.Pound)}");\n    }\n}