using System;\nusing System.Drawing;\nusing System.Drawing.Imaging;\n\nclass Program\n{\n    static void Main()\n    {\n        string inputFilePath = "input.jpg";\n        string outputFilePath = "output.jpg";\n\n        // Set new resolution (DPI)\n        float horizontalDpi = 300f; // e.g., 300 DPI for high quality printing\n        float verticalDpi = 300f;\n\n        using (Image image = Image.FromFile(inputFilePath))\n        {\n            // Create a Bitmap and modify the resolution information.\n            using (Bitmap bmp = new Bitmap(image))\n            {\n                // Set the horizontal and vertical resolutions of the bitmap to the desired values\n                bmp.SetResolution(horizontalDpi, verticalDpi);\n\n                // Save the modified bitmap to file\n                bmp.Save(outputFilePath);\n            }\n        }\n\n        Console.WriteLine("Image resolution set successfully.");\n    }\n}