using System;\nusing System.IO;\nusing System.Net;\nusing System.Text;\n\nclass Program\n{\n    static void Main()\n    {\n        string url = "https://servicios.mensario.com/enviomasivo/apip/";\n        string postData = "usuario=SomeUser&clave=SomePassword&nserie=01234567890123456789&version=01010000&operacion=220";\n        \n        // Create a request using HttpWebRequest\n        HttpWebRequest request = (HttpWebRequest)WebRequest.Create(url);\n        request.Method = "POST";\n\n        // Set the content type and length of post data being sent\n        request.ContentType = "application/x-www-form-urlencoded";\n        byte[] byteArray = Encoding.UTF8.GetBytes(postData);\n        request.ContentLength = byteArray.Length;\n\n        // Get the request stream to write data\n        using (Stream dataStream = request.GetRequestStream())\n        {\n            dataStream.Write(byteArray, 0, byteArray.Length);\n        }\n\n        // Execute the request and get the response\n        WebResponse response;\n        try\n        {\n            response = request.GetResponse();\n            \n            using (StreamReader reader = new StreamReader(response.GetResponseStream()))\n            {\n                string result = reader.ReadToEnd();\n                Console.WriteLine(result);\n            }\n        }\n        catch (WebException ex)\n        {\n            // Handle exceptions here, such as 404 Not Found\n            Console.WriteLine($"Error: {ex.Message}");\n            if (ex.Response != null)\n            {\n                using (StreamReader errorReader = new StreamReader(ex.Response.GetResponseStream()))\n                {\n                    string errorResult = errorReader.ReadToEnd();\n                    Console.WriteLine("Detailed Error: " + errorResult);\n                }\n            }\n        }\n    }\n}